import React, { useState } from 'react';
import {
  Container,
  Card,
  CardContent,
  Typography,
  Box,
  Button,
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { useNavigate } from 'react-router-dom';

const preguntas = [
  // (preguntas aquí, sin cambios...)
];

const PreguntasTeoricas = () => {
  const navigate = useNavigate();
  const [currentPage, setCurrentPage] = useState(1);
  const preguntasPorPagina = 1;
  const totalPaginas = Math.ceil(preguntas.length / preguntasPorPagina);

  const indexOfLast = currentPage * preguntasPorPagina;
  const indexOfFirst = indexOfLast - preguntasPorPagina;
  const preguntasActuales = preguntas.slice(indexOfFirst, indexOfLast);

  return (
    <Container
      maxWidth={false}
      disableGutters
      sx={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        px: 2,
        py: 4,
        position: 'relative',
      }}
    >
      <Box sx={{ position: 'absolute', top: 20, left: 20 }}>
        <Button
          variant="contained"
          startIcon={<ArrowBackIcon />}
          onClick={() => navigate('/')}
          sx={{
            borderRadius: 8,
            textTransform: 'none',
            px: 3,
            py: 1,
            bgcolor: '#4fc3f7',
            boxShadow: `
              6px 6px 12px rgba(0,0,0,0.1),
              -6px -6px 12px rgba(255,255,255,0.4)
            `,
            '&:hover': {
              bgcolor: '#039be5',
            },
          }}
        >
          Regresar
        </Button>
      </Box>

      <Typography variant="h4" fontWeight="bold" gutterBottom>
        Preguntas de la Prueba Teórica
      </Typography>

      {preguntasActuales.map((item, index) => (
        <Card
          key={index}
          sx={{
            width: {
              xs: 300,
              sm: 500,
              md: 600,
            },
            mt: 4,
            borderRadius: '20px',
            backgroundColor: 'rgba(255, 255, 255, 0.3)',
            backdropFilter: 'blur(8px)',
            WebkitBackdropFilter: 'blur(8px)',
            boxShadow: `
              6px 6px 12px rgba(0,0,0,0.1),
              -6px -6px 12px rgba(255,255,255,0.4)
            `,
            color: '#333',
          }}
        >
          <CardContent>
            <Typography variant="h6" fontWeight="bold" gutterBottom>
              {item.titulo}
            </Typography>
            <Typography variant="body2" whiteSpace="pre-line">
              {item.contenido}
            </Typography>
          </CardContent>
        </Card>
      ))}

      <Box display="flex" justifyContent="center" mt={4} gap={2}>
        {[...Array(totalPaginas).keys()].map((n) => {
          const page = n + 1;
          const isActive = page === currentPage;

          return (
            <Box
              key={page}
              onClick={() => setCurrentPage(page)}
              sx={{
                width: isActive ? 16 : 12,
                height: isActive ? 16 : 12,
                borderRadius: '50%',
                boxShadow: isActive
                  ? 'inset 4px 4px 8px #b0bec5, inset -4px -4px 8px #ffffff, 0 0 4px 2px #4fc3f7'
                  : '4px 4px 8px #b0bec5, -4px -4px 8px #ffffff',
                backgroundColor: isActive ? '#e0e0e0' : '#d6d6d6',
                cursor: 'pointer',
                transition: 'all 0.2s ease-in-out',
                '&:hover': {
                  transform: 'scale(1.2)',
                },
              }}
            />
          );
        })}
      </Box>
    </Container>
  );
};

export default PreguntasTeoricas;
