import React, { useEffect, useState } from 'react';
import { getUsers } from '../services/userService';
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  TextField,
  Box,
  Avatar,
} from '@mui/material';
import { Link } from 'react-router-dom';
import EmailIcon from '@mui/icons-material/Email';
import PhoneIcon from '@mui/icons-material/Phone';
import PersonIcon from '@mui/icons-material/Person';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const usersPerPage = 4;

  useEffect(() => {
    getUsers().then((data) => setUsers(data));
  }, []);

  const filteredUsers = users.filter((user) =>
    user.name.toLowerCase().includes(search.toLowerCase())
  );

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  return (
    <Container sx={{ py: 4 }}>
      <Typography variant="h4" textAlign="center" gutterBottom>
        Lista de Usuarios
      </Typography>

      <Box display="flex" justifyContent="center" mb={4}>
        <TextField
          label="Buscar por nombre"
          variant="outlined"
          value={search}
          onChange={(e) => {
            setSearch(e.target.value.replace(/<.*?>/g, ''));
            setCurrentPage(1); // Reiniciar a la página 1 si busca algo nuevo
          }}
          sx={{ width: '100%', maxWidth: 400 }}
        />
      </Box>

      <Grid container spacing={3}>
        {currentUsers.map((user) => (
          <Grid item xs={12} sm={6} md={4} key={user.id}>
            <Card
              component={Link}
              to={`/usuario/${user.id}`}
              sx={{
                display: 'flex',
                flexDirection: 'column',
                height: '100%',
                borderRadius: '20px',
                backgroundColor: '#e0e0e0',
                boxShadow: `
                  6px 6px 12px #bebebe,
                  -6px -6px 12px #ffffff
                `,
                color: '#333',
                textDecoration: 'none',
                transition: 'all 0.2s ease-in-out',
                '&:hover': {
                  transform: 'translateY(-4px)',
                  boxShadow: `
                    4px 4px 8px #bebebe,
                    -4px -4px 8px #ffffff
                  `,
                },
              }}
            >
              <CardContent sx={{ flexGrow: 1 }}>
                <Box display="flex" alignItems="center" mb={2}>
                  <Avatar sx={{ bgcolor: '#4fc3f7', marginRight: 2 }}>
                    <PersonIcon />
                  </Avatar>
                  <Typography variant="h6" fontWeight="600">
                    {user.name}
                  </Typography>
                </Box>
                <Box display="flex" alignItems="center" mb={1}>
                  <EmailIcon sx={{ fontSize: 18, marginRight: 1 }} />
                  <Typography variant="body2">{user.email}</Typography>
                </Box>
                <Box display="flex" alignItems="center">
                  <PhoneIcon sx={{ fontSize: 18, marginRight: 1 }} />
                  <Typography variant="body2">{user.phone}</Typography>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Controles de paginación */}
      <Box display="flex" justifyContent="center" mt={4} gap={2}>
  {[...Array(totalPages).keys()].map((n) => {
    const page = n + 1;
    const isActive = page === currentPage;

    return (
      <Box
        key={page}
        onClick={() => setCurrentPage(page)}
        sx={{
          width: isActive ? 16 : 12,
          height: isActive ? 16 : 12,
          borderRadius: '50%',
          backgroundColor: '#e0e0e0',
          boxShadow: isActive
            ? 'inset 4px 4px 8px #b0bec5, inset -4px -4px 8px #ffffff, 0 0 4px 2px #4fc3f7'
            : '6px 6px 12pxrgb(192, 12, 12), -6px -6px 12px #ffffff',
          cursor: 'pointer',
          transition: 'all 0.2s ease-in-out',
          '&:hover': {
            transform: 'scale(1.2)',
          },
        }}
      />
    );
  })}
</Box>  
    </Container>
  );
};

export default UserList;
